#include <iostream>
#include <string>

int main() {
    // Single character
    char letter = 'A';

    // Array of characters (C-style string)
    char oldSchool[] = "Hello";  // stored as H e l l o \0

    // Modern C++ string
    std::string modern = "Hello";

    std::cout << "letter: " << letter << "\n";
    std::cout << "oldSchool: " << oldSchool << "\n";
    std::cout << "modern: " << modern << "\n";

    return 0;
}
